import React from "react";
import useWindowSize from "../hooks/useWindowSize";

// Assets
import donutRight from "../assets/home_desktop/home_desktop_donut_right.png";
import donutLeft from "../assets/home_desktop/home_desk_donut_left.png";
import botRight from "../assets/home_desktop/bot_right.svg";
import botLeft from "../assets/home_desktop/bot_left.svg";
import topLeft from "../assets/home_desktop/top_left.svg";
import topRight from "../assets/home_desktop/top_right.svg";

const DESIGN_WIDTH = 1536;
const DESIGN_HEIGHT = 918;

const HomeDesktop: React.FC = () => {
  const { width, height } = useWindowSize();
  const scale = Math.min(width / DESIGN_WIDTH, height / DESIGN_HEIGHT);

  // Sample sizes in your design
  const imageSize = (w: number, h: number) => ({
    width: w * scale,
    height: h * scale,
  });

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        overflow: "hidden",
        backgroundColor: "#f9f9f9",
        position: "relative",
      }}
    >
      {/* Main scaled frame */}
      <div
        style={{
          width: DESIGN_WIDTH,
          height: DESIGN_HEIGHT,
          transformOrigin: "top left",
          transform: `scale(${scale})`,
          position: "absolute",
          top: 0,
          left: 0,
          zIndex: 1,
        }}
      >
      </div>

      {/* Corner Assets (scaled with window) */}
      <img src={botRight} alt="Bot Right" style={{ ...styles.bottomRight, ...imageSize(100, 100) }} />
      <img src={donutRight} alt="Donut Right" style={{ ...styles.bottomRight, ...imageSize(832, 150) }} />
      <img src={botLeft} alt="Bot Left" style={{ ...styles.bottomLeft, ...imageSize(100, 100) }} />
      <img src={topLeft} alt="Top Left" style={{ ...styles.topLeft, ...imageSize(1024, 1024) }} />
      <img src={donutLeft} alt="Donut Left" style={{ ...styles.topLeft, ...imageSize(829, 565) }} />
      <img src={topRight} alt="Top Right" style={{ ...styles.topRight, ...imageSize(100, 100) }} />
    </div>
  );
};

const styles: { [key: string]: React.CSSProperties } = {
  topLeft: {
    position: "absolute",
    top: 0,
    left: 0,
    zIndex: 0,
  },
  topRight: {
    position: "absolute",
    top: 0,
    right: 0,
    zIndex: 0,
  },
  bottomLeft: {
    position: "absolute",
    bottom: 0,
    left: 0,
    zIndex: 0,
  },
  bottomRight: {
    position: "absolute",
    bottom: 0,
    right: 0,
    zIndex: 0,
  },
};

export default HomeDesktop;
