// src/pages/HomeDesktop.tsx
import React, { useState } from "react";
import defaultImg from "../assets/home_desktop/default.png";
import hoverMenuImg from "../assets/home_desktop/hover_menu.png";
import hoverDeliveryImg from "../assets/home_desktop/hover_delivery.png";
import hoverPickupImg from "../assets/home_desktop/hover_pickup.png";
import hoverAboutImg from "../assets/home_desktop/hover_about.png";

const HomeDesktop: React.FC = () => {
  const [bgImage, setBgImage] = useState(defaultImg);

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        background: `url(${bgImage}) no-repeat center`,
        backgroundSize: "contain",
        position: "relative",
      }}
    >
      {/* Hover Areas */}
      <div
        onMouseEnter={() => setBgImage(hoverMenuImg)}
        onMouseLeave={() => setBgImage(defaultImg)}
        style={{
          position: "absolute",
          top: "0px",
          left: "0px",
          width: "1500px",
          height: "1500px",
          cursor: "pointer",
        }}
      />
      <div
        onMouseEnter={() => setBgImage(hoverDeliveryImg)}
        onMouseLeave={() => setBgImage(defaultImg)}
        style={{
          position: "absolute",
          top: "40px",
          left: "400px",
          width: "140px",
          height: "40px",
          cursor: "pointer",
        }}
      />
      <div
        onMouseEnter={() => setBgImage(hoverPickupImg)}
        onMouseLeave={() => setBgImage(defaultImg)}
        style={{
          position: "absolute",
          top: "40px",
          left: "560px",
          width: "130px",
          height: "40px",
          cursor: "pointer",
        }}
      />
      <div
        onMouseEnter={() => setBgImage(hoverAboutImg)}
        onMouseLeave={() => setBgImage(defaultImg)}
        style={{
          position: "absolute",
          top: "40px",
          left: "710px",
          width: "160px",
          height: "40px",
          cursor: "pointer",
        }}
      />
    </div>
  );
};

export default HomeDesktop;
