import React from "react";
import logo from "../assets/logo.png";
import left_donut from "../assets/home_desktop/home_desk_donut_left.png";
import right_donut from "../assets/home_desktop/home_desk_donut_right.png";

const HomeDesktop: React.FC = () => {
  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        overflow: "hidden",
        backgroundColor: "#eee",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          width: "1546px",
          height: "918px",
          transformOrigin: "top left",
          transform: "scale(calc(min(100vw / 1546, 100vh / 918)))",
        }}
      >
        {/* Your actual design content */}
        <header style={{ padding: "20px" }}>
          <img src={logo} alt="Logo" style={{ height: "50px" }} />
        </header>

        <section
          style={{
            backgroundImage: `url(${heroBg})`,
            height: "400px",
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        >
          <h1 style={{ color: "#fff", padding: "100px 20px" }}>
            Welcome to Our Site
          </h1>
        </section>

        <main style={{ padding: "20px" }}>
          <p>This is the desktop version of the home page.</p>
        </main>
      </div>
    </div>
  );
};

export default HomeDesktop;
