// src/pages/HomeDesktop.tsx

import React from "react";

// Assets
import donutRight from "../assets/home_desktop/home_desktop_donut_right.png";
import donutLeft from "../assets/home_desktop/home_desk_donut_left.png";
import botRight from "../assets/home_desktop/bot_right.svg";
import botLeft from "../assets/home_desktop/bot_left.svg";
import topLeft from "../assets/home_desktop/top_left.svg";
import topRight from "../assets/home_desktop/top_right.svg";

const HomeDesktop: React.FC = () => {
  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        overflow: "hidden",
        backgroundColor: "#f0f0f0",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      {/* --- Scaling Container (1536x918) --- */}
      <div
        style={{
          width: "1536px",
          height: "918px",
          transformOrigin: "top left",
          transform: "scale(calc(min(100vw / 1536, 100vh / 918)))",
          position: "relative",
        }}
      >
        {/* --- Corner Decorations (using ratio-based positioning) --- */}
        <img
          src={topRight}
          alt="Top Right Decoration"
          style={{
            position: "absolute",
            top: "0%",
            right: "0%",
          }}
        />
        <img
          src={botRight}
          alt="Bottom Right Decoration"
          style={{
            position: "absolute",
            bottom: "0%",
            right: "0%",
          }}
        />
        <img
          src={botLeft}
          alt="Bottom Left Decoration"
          style={{
            position: "absolute",
            bottom: "0%",
            left: "0%",
          }}
        />
        <img
          src={topLeft}
          alt="Top Left Decoration"
          style={{
            position: "absolute",
            top: "0%",
            left: "0%",
          }}
        />
        <img
          src={donutLeft}
          alt="Donut Left"
          style={{
            position: "absolute",
            top: "0%",
            left: "0%",
          }}
        />
        <img
          src={donutRight}
          alt="Donut Right"
          style={{
            position: "absolute",
            bottom: "0%",
            right: "0%",
          }}
        />
      </div>
    </div>
  );
};

export default HomeDesktop;
