import React, { useEffect, useRef, useState } from "react";
import defaultImg from "../assets/home_desktop/default.png";
import hoverMenuImg from "../assets/home_desktop/hover_menu.png";
import hoverDeliveryImg from "../assets/home_desktop/hover_delivery.png";
import hoverPickupImg from "../assets/home_desktop/hover_pickup.png";
import hoverAboutImg from "../assets/home_desktop/hover_about.png";

const BASE_WIDTH = 1546;
const BASE_HEIGHT = 918;

const HomeDesktop: React.FC = () => {
  const [imgSrc, setImgSrc] = useState(defaultImg);
  const containerRef = useRef<HTMLDivElement>(null);
  const [scale, setScale] = useState(1);

  // Recalculate scale on resize
  useEffect(() => {
    const updateScale = () => {
      if (containerRef.current) {
        const { clientWidth, clientHeight } = containerRef.current.parentElement!;
        const scaleWidth = clientWidth / BASE_WIDTH;
        const scaleHeight = clientHeight / BASE_HEIGHT;
        setScale(Math.min(scaleWidth, scaleHeight));
      }
    };

    window.addEventListener("resize", updateScale);
    updateScale(); // initial

    return () => window.removeEventListener("resize", updateScale);
  }, []);

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "#000",
        overflow: "hidden",
      }}
    >
      <div
        ref={containerRef}
        style={{
          width: BASE_WIDTH,
          height: BASE_HEIGHT,
          position: "relative",
          transform: `scale(${scale})`,
          transformOrigin: "top left",
        }}
      >
        <img
          src={imgSrc}
          alt="Home Desktop"
          style={{
            width: BASE_WIDTH,
            height: BASE_HEIGHT,
            display: "block",
          }}
        />

        {/* Hover Zones */}
        <div
          onMouseEnter={() => setImgSrc(hoverMenuImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 40,
            left: 250,
            width: 120,
            height: 40,
            cursor: "pointer",
          }}
        />
        <div
          onMouseEnter={() => setImgSrc(hoverDeliveryImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 40,
            left: 400,
            width: 140,
            height: 40,
            cursor: "pointer",
          }}
        />
        <div
          onMouseEnter={() => setImgSrc(hoverPickupImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 40,
            left: 560,
            width: 130,
            height: 40,
            cursor: "pointer",
          }}
        />
        <div
          onMouseEnter={() => setImgSrc(hoverAboutImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 40,
            left: 710,
            width: 160,
            height: 40,
            cursor: "pointer",
          }}
        />
      </div>
    </div>
  );
};

export default HomeDesktop;
