import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import defaultImg from "../assets/home_desktop/default.png";
import hoverMenuImg from "../assets/home_desktop/hover_menu.png";
import hoverDeliveryImg from "../assets/home_desktop/hover_delivery.png";
import hoverPickupImg from "../assets/home_desktop/hover_pickup.png";
import hoverAboutImg from "../assets/home_desktop/hover_about.png";

const DESIGN_WIDTH = 1546;
const DESIGN_HEIGHT = 918;

const HomeDesktop: React.FC = () => {
  const [imgSrc, setImgSrc] = useState(defaultImg);
  const [scale, setScale] = useState(1);

  const updateScale = () => {
    const widthScale = window.innerWidth / DESIGN_WIDTH;
    const heightScale = window.innerHeight / DESIGN_HEIGHT;
    setScale(Math.min(widthScale, heightScale));
  };

  useEffect(() => {
    updateScale();
    window.addEventListener("resize", updateScale);
    return () => window.removeEventListener("resize", updateScale);
  }, []);

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        backgroundColor: "#D1D1D1",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          width: DESIGN_WIDTH * scale,
          height: DESIGN_HEIGHT * scale,
          position: "relative",
        }}
      >
        <img
          src={imgSrc}
          alt="Home"
          style={{
            width: "100%",
            height: "100%",
            objectFit: "contain",
            display: "block",
          }}
        />

        {/* Menu Hover Area */}
        <Link
          to="/menu"
          onMouseEnter={() => setImgSrc(hoverMenuImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 0 * scale,
            left: 0 * scale,
            width: 120 * scale,
            height: 40 * scale,
            display: "block",
            cursor: "pointer",
          }}
        />

        {/* Delivery Hover Area */}
        <Link
          to="/delivery"
          onMouseEnter={() => setImgSrc(hoverDeliveryImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 40 * scale,
            left: 400 * scale,
            width: 140 * scale,
            height: 40 * scale,
            display: "block",
            cursor: "pointer",
          }}
        />

        {/* Pickup Hover Area */}
        <Link
          to="/pickup"
          onMouseEnter={() => setImgSrc(hoverPickupImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 40 * scale,
            left: 560 * scale,
            width: 130 * scale,
            height: 40 * scale,
            display: "block",
            cursor: "pointer",
          }}
        />

        {/* About Us Hover Area */}
        <Link
          to="/about"
          onMouseEnter={() => setImgSrc(hoverAboutImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 40 * scale,
            left: 710 * scale,
            width: 160 * scale,
            height: 40 * scale,
            display: "block",
            cursor: "pointer",
          }}
        />
      </div>
    </div>
  );
};

export default HomeDesktop;
