// src/pages/HomeDesktop.tsx

import React from "react";

// Example assets
import donutRight from "../assets/home_desktop/home_desktop_donut_right.png";
import donutLeft from "../assets/home_desktop/home_desk_donut_left.png";
import botRight from "../assets/home_desktop/bot_right.svg";
import botLeft from "../assets/home_desktop/bot_left.svg";
import topLeft from "../assets/home_desktop/top_left.svg";
import topRight from "../assets/home_desktop/top_right.svg";

const ASPECT_WIDTH = 1600;
const ASPECT_HEIGHT = 1000;

const HomeDesktop: React.FC = () => {
  return (
    <div style={styles.screenContainer}>
      {/* 16:10 FRAME */}
      <div style={styles.scalingFrame}>
        <div style={styles.frameContent}>
          <h1 style={{ padding: "50px" }}>16:10 Frame Content</h1>
        </div>
      </div>

      {/* Decorations around the frame */}
      <img src={topLeft} alt="Top Left" style={styles.topLeft} />
      <img src={donutLeft} alt="Top Left Donut" style={styles.topLeft} />
      <img src={topRight} alt="Top Right" style={styles.topRight} />
      <img src={botLeft} alt="Bottom Left" style={styles.bottomLeft} />
      <img src={donutRight} alt="Bottom Right Donut" style={styles.bottomRight} />
      <img src={botRight} alt="Bottom Right" style={styles.bottomRight} />
    </div>
  );
};

// ðŸ§  Dynamic scale based on aspect ratio
const styles: { [key: string]: React.CSSProperties } = {
  screenContainer: {
    width: "100vw",
    height: "100vh",
    overflow: "hidden",
    position: "relative",
    background: "#f5f5f5",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  scalingFrame: {
    width: `${ASPECT_WIDTH}px`,
    height: `${ASPECT_HEIGHT}px`,
    transform: `scale(calc(min(100vw / ${ASPECT_WIDTH}, 100vh / ${ASPECT_HEIGHT})))`,
    transformOrigin: "top left",
    position: "relative",
    zIndex: 1,
  },
  frameContent: {
    width: "100%",
    height: "100%",
    backgroundColor: "#fff",
    boxShadow: "0 0 30px rgba(0,0,0,0.1)",
    borderRadius: "12px",
    overflow: "hidden",
  },
  topLeft: {
    position: "absolute",
    top: 0,
    left: 0,
    zIndex: 0,
  },
  topRight: {
    position: "absolute",
    top: 0,
    right: 0,
    zIndex: 0,
  },
  bottomLeft: {
    position: "absolute",
    bottom: 0,
    left: 0,
    zIndex: 0,
  },
  bottomRight: {
    position: "absolute",
    bottom: 0,
    right: 0,
    zIndex: 0,
  },
};

export default HomeDesktop;
