import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import defaultImg from "../assets/home_mobile/default.png";

const DESIGN_WIDTH = 640;
const DESIGN_HEIGHT = 1136;

const HomeMobile: React.FC = () => {
  const [scale, setScale] = useState(1);

  const updateScale = () => {
    const widthScale = window.innerWidth / DESIGN_WIDTH;
    const heightScale = window.innerHeight / DESIGN_HEIGHT;
    setScale(Math.min(widthScale, heightScale));
  };

  useEffect(() => {
    updateScale();
    window.addEventListener("resize", updateScale);
    return () => window.removeEventListener("resize", updateScale);
  }, []);

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        backgroundColor: "#D1D1D1",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          width: DESIGN_WIDTH * scale,
          height: DESIGN_HEIGHT * scale,
          position: "relative",
        }}
      >
        <img
          src={defaultImg}
          alt="Home Mobile"
          style={{
            width: "100%",
            height: "100%",
            objectFit: "contain",
            display: "block",
          }}
        />

        {/* Example: Menu Link */}
        <a
          href="https://menu.ringocoffee.com"
          target="_blank"
          rel="noopener noreferrer"
          style={{
            position: "absolute",
            top: 50 * scale,
            left: 20 * scale,
            width: 280 * scale,
            height: 40 * scale,
            display: "block",
            cursor: "pointer",
          }}
        />

        {/* Example: Delivery Link */}
        <a
          href="https://www.order.store/store/ringo-coffee-and-donuts-philadelphia/hgQweTNMTNeUQFJh-_ovmg"
          target="_blank"
          rel="noopener noreferrer"
          style={{
            position: "absolute",
            top: 110 * scale,
            left: 20 * scale,
            width: 280 * scale,
            height: 40 * scale,
            display: "block",
            cursor: "pointer",
          }}
        />

        {/* Example: Pickup Link */}
        <a
          href="https://ringo-coffee-donutsorder.square.site/"
          target="_blank"
          rel="noopener noreferrer"
          style={{
            position: "absolute",
            top: 0 * scale,
            left: 0 * scale,
            width: 205 * scale,
            height: 460 * scale,
            display: "block",
            cursor: "pointer",
          }}
        />

        {/* Example: About Us internal link */}
        <Link
          to="/about"
          style={{
            position: "absolute",
            top: 230 * scale,
            left: 20 * scale,
            width: 280 * scale,
            height: 40 * scale,
            display: "block",
            cursor: "pointer",
          }}
        />
      </div>
    </div>
  );
};

export default HomeMobile;
