// src/pages/HomeDesktop.tsx

import React, { useEffect, useState } from "react";

// Assets
import donutRight from "../assets/home_desktop/home_desktop_donut_right.png";
import donutLeft from "../assets/home_desktop/home_desk_donut_left.png";
import botRight from "../assets/home_desktop/bot_right.svg";
import botLeft from "../assets/home_desktop/bot_left.svg";
import topLeft from "../assets/home_desktop/top_left.svg";
import topRight from "../assets/home_desktop/top_right.svg";

const DESIGN_WIDTH = 1536;
const DESIGN_HEIGHT = 918;
const MIN_SCALE = 0.5;

const HomeDesktop: React.FC = () => {
  const [scale, setScale] = useState(1);

  useEffect(() => {
    const updateScale = () => {
      const scaleWidth = window.innerWidth / DESIGN_WIDTH;
      const scaleHeight = window.innerHeight / DESIGN_HEIGHT;
      const calculatedScale = Math.min(scaleWidth, scaleHeight);
      setScale(Math.max(MIN_SCALE, calculatedScale));
    };

    updateScale();
    window.addEventListener("resize", updateScale);
    return () => window.removeEventListener("resize", updateScale);
  }, []);

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        overflow: scale <= MIN_SCALE ? "auto" : "hidden",
        backgroundColor: "#f0f0f0",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      {/* --- Scaling Container (1536x918) --- */}
      <div
        style={{
          width: `${DESIGN_WIDTH}px`,
          height: `${DESIGN_HEIGHT}px`,
          transformOrigin: "top left",
          transform: `scale(${scale})`,
          position: "relative",
        }}
      >
        {/* --- Corner Decorations (positioned inside scaled frame) --- */}
        <img src={donutRight} alt="Donut Right" style={{ position: "absolute", bottom: 0, right: 0 }} />
        <img src={botRight} alt="Bottom Right Decoration" style={{ position: "absolute", bottom: 0, right: 0 }} />
        <img src={botLeft} alt="Bottom Left Decoration" style={{ position: "absolute", bottom: 0, left: 0 }} />
        <img src={topLeft} alt="Top Left Decoration" style={{ position: "absolute", top: 0, left: 0 }} />
        <img src={donutLeft} alt="Donut Left" style={{ position: "absolute", top: 0, left: 0 }} />
        <img src={topRight} alt="Top Right Decoration" style={{ position: "absolute", top: 0, right: 0 }} />
      </div>
    </div>
  );
};

export default HomeDesktop;
