import React, { useState } from "react";
import defaultImg from "../assets/home_desktop/default.png";
import hoverMenuImg from "../assets/home_desktop/hover_menu.png";
import hoverDeliveryImg from "../assets/home_desktop/hover_delivery.png";
import hoverPickupImg from "../assets/home_desktop/hover_pickup.png";
import hoverAboutImg from "../assets/home_desktop/hover_about.png";

const WIDTH = 1546;
const HEIGHT = 918;

const HomeDesktop: React.FC = () => {
  const [imgSrc, setImgSrc] = useState(defaultImg);

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "#000", // optional: black background
      }}
    >
      <div style={{ width: WIDTH, height: HEIGHT, position: "relative" }}>
        <img
          src={imgSrc}
          alt="Home"
          style={{
            width: "100%",
            height: "100%",
            display: "block",
            objectFit: "cover",
          }}
        />

        {/* Hover Regions */}
        <div
          onMouseEnter={() => setImgSrc(hoverMenuImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: 120,
            height: 40,
            cursor: "pointer",
          }}
        />
        <div
          onMouseEnter={() => setImgSrc(hoverDeliveryImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 40,
            left: 400,
            width: 140,
            height: 40,
            cursor: "pointer",
          }}
        />
        <div
          onMouseEnter={() => setImgSrc(hoverPickupImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 40,
            left: 560,
            width: 130,
            height: 40,
            cursor: "pointer",
          }}
        />
        <div
          onMouseEnter={() => setImgSrc(hoverAboutImg)}
          onMouseLeave={() => setImgSrc(defaultImg)}
          style={{
            position: "absolute",
            top: 40,
            left: 710,
            width: 160,
            height: 40,
            cursor: "pointer",
          }}
        />
      </div>
    </div>
  );
};

export default HomeDesktop;
