import React from "react";
import logo from "../assets/logo.png";
import donutRight from "../assets/home_desktop/home_desktop_donut_right.png";
import donutLeft from "../assets/home_desktop/home_desk_donut_left.png";
import botRight from "../assets/home_desktop/bot_right.svg";
import botLeft from "../assets/home_desktop/bot_left.svg";
import topLeft from "../assets/home_desktop/top_left.svg";
import topRight from "../assets/home_desktop/top_right.svg";

const HomeDesktop: React.FC = () => {
  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        overflow: "hidden",
        backgroundColor: "#f0f0f0",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        position: "relative",
      }}
    >
      {/* --- Scaling Container --- */}
      <div
        style={{
          width: "1546px",
          height: "918px",
          transformOrigin: "top left",
          transform: "scale(calc(min(100vw / 1546, 100vh / 918)))",
          position: "relative",
          zIndex: 1,
        }}
      >
      </div>

      {/* --- Corner Decorations --- */}
      <img src={botRight} alt="Bottom Right Decoration" style={styles.bottomRight} />
      <img src={donutRight} alt="Donut Right" style={styles.bottomRight} />
      <img src={botLeft} alt="Bottom Left Decoration" style={styles.bottomLeft} />
      <img src={topLeft} alt="Top Left Decoration" style={styles.topLeft} />
      <img src={donutLeft} alt="Donut Left" style={styles.topLeft} />
      <img src={topRight} alt="Top Right Decoration" style={styles.topRight} />
    </div>
  );
};

// --- Positioning Styles ---
const styles: { [key: string]: React.CSSProperties } = {
  topLeft: {
    position: "absolute",
    top: 0,
    left: 0,
    zIndex: 0,
  },
  topRight: {
    position: "absolute",
    top: 0,
    right: 0,
    zIndex: 0,
  },
  bottomLeft: {
    position: "absolute",
    bottom: 0,
    left: 0,
    zIndex: 0,
  },
  bottomRight: {
    position: "absolute",
    bottom: 0,
    right: 0,
    zIndex: 0,
  },
};

export default HomeDesktop;
